package ch01;


public class Operate2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = ++a;
		System.out.printf("a = %d, b = %d ",a,b);
		System.out.println();
		
		int c = 10;
		int d = c++;
		System.out.printf("c : %d , d : %d ",c,d);
		
		
		

	}

}
