package ch01;

public class Operate3 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 11;
		System.out.println("a == b ? "+(a == b));
		System.out.println("a != b ? "+(a != b));
		System.out.println("a > b ? "+(a > b));
		System.out.println("a < b ? "+(a < b));
		
		//결과를 result(boolean)에 담자
		boolean result = a==b;
		System.out.println("result : "+result);
		

	}

}
