package ch01;

public class Operate6 {

public static void main(String[] args) {
	// TODO Auto-generated method stub

	int a = 1;
	
	a += 1;
	System.out.println(a); //1+1 == 2 
	
	a -= 1;
	System.out.println(a); // 2-1 ==  1
	
	a *= 4;
	System.out.println(a); // 1*4 == 4
	
	a /= 2;
	System.out.println(a); // 4/2 == 2
	
}

}
